
public class NumberCalc {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 숫자 50과 30의 덧셈, 뺄셈, 곱셈, 나눗셈의 결과를 콘솔에 표시하는 코드를 지금부터 작성 해 보세요
	System.out.println("50+30="+(50+30));
	System.out.println();
	System.out.println("50-30="+(50-30));
	System.out.println();
	System.out.println("50*30="+(50*30));
	System.out.println();
	System.out.println("50/30="+(50/30));
	// 나누기(/)는 몫을 구하는 연산으로 소수점은 버림되고, %는몫을 뺀 나머지를 구하는 연산이다.
	System.out.println("50%30="+(50%30));

	}

}
